            document.addEventListener('DOMContentLoaded', function(){
                function getParameterByName(name, url) {
                    name = name.replace(/[\[\]]/g, '\$&');
                    var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)'),
                        results = regex.exec(url);
                    if (!results) return null;
                    if (!results[2]) return '';
                    return decodeURIComponent(results[2].replace(/\+/g, ' '));
                }

                document.addEventListener('click', function(event){
                    const namedItem = event.target.attributes.getNamedItem('data-product_id')
                    if(!event.target.matches('.ajax_add_to_cart')){
                        return;
                    }

                    var item_id = null;

                    if(!namedItem){
                        try{
                            item_id = getParameterByName('add-to-cart', new URL(event.target.href).search)
                        }
                        catch(e){}
                    }
                    else{
                        item_id = namedItem.value
                    }

                    if(item_id != C-M-S-250){
                        return;
                    }

                    gtag('event', 'add_to_cart', {'items': [ {"item_id":"C-M-S-250","item_name":"Cleaning Me Softly","list_name":"Cleaning Me Softly","quantity":1,"price":25,"item_category":"Bimbi","item_category_2":"Corpo","item_category_3":"Detergenti &amp; Struccanti"} ]});
                })

            });

